import React, { Component } from 'react'
import {movies} from './getMovies'
import axios from 'axios';
export default class Movies extends Component {
  render() {
      let movie = movies.results
    return (
      <>
      {
          movie.length == 0?
          <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
          </div>:
        <div>
            <h3 className='test-center'><strong>Trending</strong></h3>
           <div>
             {
               movie.map((movieObj)=>(
                <div className="card banner-card" >
                <img src={`https://image.tmdb.org/t/p/original${movieObj.backdrop_path}`} class="card-img-top banner-img" alt={movie.title}/>
                <div className="card-body">
                  <h1 className="card-title banner-title">{movieObj.original_title}</h1>
                  <h1 className="card-title banner-title">{movieObj.original_name}</h1>
                  <p className="card-text banner-text">{movieObj.overview}</p>
                  <a href="#" className="btn btn-primary">Add To Favorite</a>
              </div>
              </div>
               ))
             }
           </div>

        </div>
      }
      </>
    )
  }
}
