import React, { Component } from 'react'
import {movies} from './getMovies'
import axios from 'axios';

export default class Banner extends Component {
  async componentDidMount(){
    //side effects 
    const res = await axios.get(`https://api.themoviedb.org/3/trending/all/day?api_key=f63939183c4047ef43886b40a47ce3bc&language=en&page=${this.state.currPage}`)
    let data = res.data
    this.setState({
      movies:[...data.results]
    })
  }
  render() {
    let movie = this.state.movies.results[0]
    return (
        <>
        {

            movie.length == 0?
                <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
                </div>:
            <div className="card banner-card" >
              <img src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`} class="card-img-top banner-img" alt={movie.title}/>
              <div className="card-body">
              <h1 className="card-title banner-title">{movie.original_title}</h1>
              <h1 className="card-title banner-title">{movie.original_name}</h1>
              <p className="card-text banner-text">{movie.overview}</p>
              {/* <a href="#" className="btn btn-primary">Go somewhere</a> */}
            </div>
            </div>
        }
      </>
    )
  }
}
